u32 GetUIntFormString(String string, b32 *success)
{
	u32 sum = 0;
	u32 exp = 0;
	for (u32 i = 0; i < string.length; i++)
	{
		u32 charToUInt = string[i] - '0';
		if (charToUInt < 10)
		{
			sum = 10 * sum + charToUInt;
		}
		else
		{
			*success = false;
		}
	}
	return sum;
}

static void HelloHelper(StringArray args)
{
	ConsoleOutputString("Hello!");
}

static void LorumHelper(StringArray args)
{
	ConsoleOutputString(lorum);
}

static void AddHelper(StringArray args)
{
	b32 success = true;
	u32 inp1 = GetUIntFormString(args[0], &success);
	u32 inp2 = GetUIntFormString(args[1], &success);

	if (!success)
	{
		ConsoleOutputString("Arguments did not match!");
		return;
	}

	u32 sum = inp1 + inp2;
	const size_t bufLength = 200;
	char *buffer = PushArray(workingArena, char, bufLength);
	u32 length = snprintf((char * const)buffer, bufLength, "%i + %i = %i", inp1, inp2, sum);
	String output = CreateString(buffer, length);
	ConsoleOutputString(output);
}


static void ClearHelper(StringArray args)
{
	console.commandHistory->length = 0;
	console.historyLength = 0;
	console.historyPos = 0;
	Clear(console.arena);
}

